import React, {useState} from 'react';
import socketClient  from "socket.io-client";
const SERVER = "http://127.0.0.1:8080";
function App() {
	const [currentTime,setCurrentTime] = useState('nothing yet')
	const [paused, setPause] = useState(false)
    var socket = socketClient (SERVER);
	socket.on('connection', () => {
		//:		console.log("connected with backend")
	})
	socket.on('timestamp', (response) => {setCurrentTime(response)});
	socket.on('pause', (response) => {setPause(response)});




	function togglePause() {
		if(paused === false) {
			socket.emit('pause')
		} else {
			socket.emit('unpause')
		}
	}
    return (
        <div classname="App">
			{currentTime}
			<button onClick= {togglePause}> Pause/Unpaue </button>
		</div>
	)
}

export default App;
