import React, { useState, useEffect, useCallback } from "react";
import socketClient from "socket.io-client";
import Main from "./Main"
import {SocketContext, socket} from 'context/socket';
function App() {
  const [currentTime, setCurrentTime] = useState(0);
  const [paused, setPause] = useState(false);
  const [updatePause, setUpdatePause] = useState(false);
  useEffect(() => {
    var socket = socketClient(SERVER);
    socket.on("connection", () => {
      console.log("connected with backend");
    });

    socket.on("timestamp", (response) => {
      setCurrentTime(response);
    });
    socket.on("pause", pauseFunction);

    if (updatePause === true) {
      setUpdatePause(false);
      if (paused === false) {
        socket.emit("pause", currentTime);
      } else {
        socket.emit("unpause", currentTime);
      }
    }
    return () => {
      socket.disconnect();
    };
  }, []);

  pauseFunction = (response) => {
    console.log("recieved pause brodcast", response);
    setPause(response.bool);
    setCurrentTime(response.time);
  };

  function togglePause() {
    if (paused === false) {
      setPause(true);
      setUpdatePause(true);
    } else {
      setPause(false);
      setUpdatePause(true);
    }
  }

  return (
    <div classname="App">
      {currentTime}
      <button onClick={togglePause}> Pause/Unpaue </button>
    </div>
  );
}

export default App;
